                    if (widget) {
                        if (this.admin || this.preview) {
                            coords = widget.getRelativeCoordinates($('PageWrapper'));
                            if (top == 0) {
                                top = coords.top;
                            } else {
                                top = Math.min(top, coords.top);
                            }
                        } else {
                            coords = widget.getCoordinates();
                            if (top == 0) {
                                top = coords.top;
                            } else {
                                top = Math.min(top, coords.top);
                            }
                        }
                    }
